import "./NextVideo.scss"
function NextVideo({props}){
    //filter current playing video and donot display it on next video list
   const flieredVideos=props.video.videos.filter(video=>video.id!==props.video.currentVideoId);
    return(
        <div className="video">
            {flieredVideos.map(video=>(
                <div key={props.video.id} className="vedio_list" onClick={()=>onSelect(video.id)}>
                    <img src={props.video.image} alt={props.video.title}/>
                    <h2>{props.video.title}</h2>
                    <p>{props.video.channel}</p>
                </div>
        ))}
        </div>
    )
}
export default NextVideo
